import { useState } from 'react';
import axios from 'axios';

function SubmitComplaint() {
  const [formData, setFormData] = useState({ title: '', description: '', name: '', contact: '' });
  const [ticketId, setTicketId] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/complaints', {
        title: formData.title,
        description: formData.description,
        name: formData.name,
        contact: formData.contact
      });
      setTicketId(response.data.ticketId);
      setFormData({ title: '', description: '', name: '', contact: '' });
      alert('Complaint submitted! Check your Ticket ID.');
    } catch (err) {
      console.error('Error:', err);
      alert('Failed to submit complaint');
    }
  };

  return (
    <div>
      <h2>Submit a Complaint</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label><br />
          <input
            type="text"
            value={formData.title}
            onChange={(e) => setFormData({ ...formData, title: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Description:</label><br />
          <textarea
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Name:</label><br />
          <input
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Contact (Email/Phone):</label><br />
          <input
            type="text"
            value={formData.contact}
            onChange={(e) => setFormData({ ...formData, contact: e.target.value })}
            required
          />
        </div>
        <button type="submit">Submit</button>
      </form>
      {ticketId && (
        <p>
          Complaint submitted! Your Ticket ID is: <strong>{ticketId}</strong>
        </p>
      )}
    </div>
  );
}

export default SubmitComplaint;
