import { useState } from 'react';
import { Form, Button, Container, Card } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { toast } from 'react-toastify';

function TrackStatus() {
  const [ticketId, setTicketId] = useState('');
  const [complaint, setComplaint] = useState(null);
  const token = localStorage.getItem('token');

  const handleTrack = async (e) => {
    e.preventDefault();
    if (!token) {
      // Guest mode: Simulate tracking
      if (ticketId.startsWith('GUEST-')) {
        setComplaint({
          ticketId,
          title: 'Guest Complaint',
          description: 'This is a guest submission (simulated due to backend issues).',
          status: 'Submitted',
          response: 'Awaiting processing (simulated).'
        });
        toast.info('Tracking simulated in guest mode.');
      } else {
        toast.error('Invalid guest ticket ID. Guest tickets start with "GUEST-".');
      }
    } else {
      // Authenticated user: Attempt API call (will fail if backend is down)
      try {
        const response = await fetch(`https://citizen-engagement-backend.onrender.com/api/complaints/${ticketId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setComplaint(data);
        } else {
          throw new Error(data.message || 'Complaint not found');
        }
      } catch (err) {
        console.error('Error:', err);
        toast.error('Failed to track complaint');
      }
    }
  };

  return (
    <Container>
      <h2 className="text-primary">Track Your Complaint</h2>
      <Form onSubmit={handleTrack}>
        <Form.Group className="mb-3">
          <Form.Label>Ticket ID</Form.Label>
          <Form.Control
            type="text"
            value={ticketId}
            onChange={(e) => setTicketId(e.target.value)}
            placeholder="Enter your ticket ID"
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          <FontAwesomeIcon icon={faSearch} className="me-2" /> Track
        </Button>
      </Form>
      {complaint && (
        <Card className="mt-3">
          <Card.Body>
            <Card.Title>Ticket ID: {complaint.ticketId}</Card.Title>
            <Card.Text>
              <strong>Title:</strong> {complaint.title}<br />
              <strong>Description:</strong> {complaint.description}<br />
              <strong>Status:</strong> {complaint.status}<br />
              <strong>Response:</strong> {complaint.response || 'None'}
            </Card.Text>
          </Card.Body>
        </Card>
      )}
    </Container>
  );
}

export default TrackStatus;
