import { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Card, Row, Col } from 'react-bootstrap';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Analytics() {
  const [analytics, setAnalytics] = useState({ byCategory: [], byStatus: [], byAgency: [] });

  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get('http://localhost:5000/api/admin/analytics', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => setAnalytics(response.data))
      .catch(err => console.error('Error:', err));
  }, []);

  // Chart data for categories
  const categoryData = {
    labels: analytics.byCategory.map(item => item._id),
    datasets: [{
      label: 'Complaints by Category',
      data: analytics.byCategory.map(item => item.count),
      backgroundColor: 'rgba(54, 162, 235, 0.6)',
      borderColor: 'rgba(54, 162, 235, 1)',
      borderWidth: 1
    }]
  };

  // Chart data for status
  const statusData = {
    labels: analytics.byStatus.map(item => item._id),
    datasets: [{
      label: 'Complaints by Status',
      data: analytics.byStatus.map(item => item.count),
      backgroundColor: 'rgba(75, 192, 192, 0.6)',
      borderColor: 'rgba(75, 192, 192, 1)',
      borderWidth: 1
    }]
  };

  // Chart data for agency
  const agencyData = {
    labels: analytics.byAgency.map(item => item._id),
    datasets: [{
      label: 'Complaints by Agency',
      data: analytics.byAgency.map(item => item.count),
      backgroundColor: 'rgba(153, 102, 255, 0.6)',
      borderColor: 'rgba(153, 102, 255, 1)',
      borderWidth: 1
    }]
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { position: 'top' },
      title: { display: true }
    }
  };

  return (
    <Container>
      <h2>Complaint Analytics</h2>
      <Row>
        <Col md={4}>
          <Card className="mb-3">
            <Card.Body>
              <Card.Title>Complaints by Category</Card.Title>
              <Bar data={categoryData} options={{ ...chartOptions, plugins: { ...chartOptions.plugins, title: { text: 'Complaints by Category' } } }} />
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="mb-3">
            <Card.Body>
              <Card.Title>Complaints by Status</Card.Title>
              <Bar data={statusData} options={{ ...chartOptions, plugins: { ...chartOptions.plugins, title: { text: 'Complaints by Status' } } }} />
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="mb-3">
            <Card.Body>
              <Card.Title>Complaints by Agency</Card.Title>
              <Bar data={agencyData} options={{ ...chartOptions, plugins: { ...chartOptions.plugins, title: { text: 'Complaints by Agency' } } }} />
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default Analytics;
