import { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Container, ListGroup } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye } from '@fortawesome/free-solid-svg-icons';

function Dashboard() {
  const [contact, setContact] = useState('');
  const [complaints, setComplaints] = useState([]);

  const handleFetchComplaints = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5000/api/complaints/user/complaints?contact=${contact}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setComplaints(response.data);
    } catch (err) {
      console.error('Error:', err);
      alert('Failed to fetch complaints');
      throw new Error('Test')


    }
  };

  return (
    <Container>
      <h2 className="text-primary">My Complaints Dashboard</h2>
      <Form onSubmit={handleFetchComplaints}>
        <Form.Group className="mb-3">
          <Form.Label>Contact (Email)</Form.Label>
          <Form.Control
            type="text"
            value={contact}
            onChange={(e) => setContact(e.target.value)}
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          <FontAwesomeIcon icon={faEye} className="me-2" /> View Complaints
        </Button>
      </Form>
      {complaints.length > 0 && (
        <div className="mt-3">
          <h3>Recent Complaints</h3>
          <ListGroup>
            {complaints.map(complaint => (
              <ListGroup.Item key={complaint._id}>
                <strong>{complaint.title}</strong> (Ticket: {complaint.ticketId})<br />
                Status: {complaint.status} | Response: {complaint.response || 'None'}<br />
                Category: {complaint.category} | Agency: {complaint.agency}<br />
                Description: {complaint.description}<br />
                Submitted: {new Date(complaint.createdAt).toLocaleString()}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </div>
      )}
    </Container>
  );
}

export default Dashboard;
