import { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Card, Row, Col } from 'react-bootstrap';

function Analytics() {
  const [analytics, setAnalytics] = useState({ byCategory: [], byStatus: [], byAgency: [] });

  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get('http://localhost:5000/api/admin/analytics', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => setAnalytics(response.data))
      .catch(err => console.error('Error:', err));
  }, []);

  return (
    <Container>
      <h2>Complaint Analytics</h2>
      <Row>
        <Col md={4}>
          <Card className="mb-3">
            <Card.Body>
              <Card.Title>Complaints by Category</Card.Title>
              <ul>
                {analytics.byCategory.map(item => (
                  <li key={item._id}>{item._id}: {item.count}</li>
                ))}
              </ul>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="mb-3">
            <Card.Body>
              <Card.Title>Complaints by Status</Card.Title>
              <ul>
                {analytics.byStatus.map(item => (
                  <li key={item._id}>{item._id}: {item.count}</li>
                ))}
              </ul>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="mb-3">
            <Card.Title>Complaints by Agency</Card.Title>
            <ul>
              {analytics.byAgency.map(item => (
                <li key={item._id}>{item._id}: {item.count}</li>
              ))}
            </ul>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default Analytics;
