import { useState, useEffect } from 'react';
import axios from 'axios';

function Analytics() {
  const [analytics, setAnalytics] = useState({ byCategory: [], byStatus: [], byAgency: [] });

  useEffect(() => {
    axios.get('http://localhost:5000/api/admin/analytics')
      .then(response => setAnalytics(response.data))
      .catch(err => console.error('Error:', err));
  }, []);

  return (
    <div>
      <h2>Complaint Analytics</h2>
      <h3>Complaints by Category</h3>
      <ul>
        {analytics.byCategory.map(item => (
          <li key={item._id}>{item._id}: {item.count}</li>
        ))}
      </ul>
      <h3>Complaints by Status</h3>
      <ul>
        {analytics.byStatus.map(item => (
          <li key={item._id}>{item._id}: {item.count}</li>
        ))}
      </ul>
      <h3>Complaints by Agency</h3>
      <ul>
        {analytics.byAgency.map(item => (
          <li key={item._id}>{item._id}: {item.count}</li>
        ))}
      </ul>
    </div>
  );
}

export default Analytics;
